import { useState } from "react";

const Myhook1=()=>{
    let citylist=["Bangalore","Mumbai","Pune","Channei"];
    const[Bangalore, Mumbai, a, b] = citylist;

    // console.log(useState());

    // let[abc ,xyz]=[undefined];
    

    let[counter, updateCounter]= useState(1100);
    let[msg, updateMsg] = useState("");
    let[user, updateUser]= useState( ['Alex','Ganesh','Ravi','Mohit','Sam'] );

    const one =()=>{
        updateCounter( counter + 10);
        updateMsg("The Counter is incresed - "+counter);
       
    }
    
    const two =()=>{
        updateCounter( counter - 5);
        updateMsg("Sorry , Counter is Moving Down...  "+counter);
       
    }

    let rate = 10;



    

    return(
        <div className="container">
            <h1>How to use usestate () Hook & What is State Managemnent </h1>
            <p> {citylist[0]} </p>
            <p> {Bangalore} </p>
            <p> {b} </p>
            <p> {a} </p>
            <h1>The Counter is :{counter}</h1>
            <button onClick={ one }>Click to + by 10</button>
            <button onClick={ two }>Click to - by 5</button>

            <h2> {msg} </h2>
            

            <p> {rate *  counter} </p>
            <p> {rate *counter +   50} </p>
            <p> {rate *  50  +counter } </p>

            {
                user.map((name, index)=>{
                    return(
                        <p key={index}> {name} </p>
                    )
                })
            }
        </div>
    )

    

}
export default Myhook1;
